    class TransferEventResponse : BaseEventResponse() {
        var from: String? = null
        var to: String? = null
        var tokenId: BigInteger? = null
    }

    /**
     * There is a difference between contract binary creation and binary runtime. This is a creation binary
     * */
    object Const {
        const val CONTRACT_BINARY = ""

        val TRANSFER_EVENT = Event(
            "Transfer",
            Arrays.asList<TypeReference<*>>(
                object : TypeReference<Address?>(true) {},
                object : TypeReference<Address?>(true) {},
                object : TypeReference<Uint256?>(true) {}
            )
        )
    }

    companion object {
        @JvmStatic
        fun load(
            contractAddress: String?,
            web3j: Web3j?,
            credentials: Credentials?,
            contractGasProvider: ContractGasProvider?
        ): SwapValue {
            return SwapValue(Const.CONTRACT_BINARY, contractAddress, web3j, credentials, contractGasProvider)
        }

        @JvmStatic
        fun load(
            contractAddress: String?,
            web3j: Web3j?,
            transactionManager: TransactionManager,
            contractGasProvider: ContractGasProvider?
        ): SwapValue {
            return SwapValue(Const.CONTRACT_BINARY, contractAddress, web3j, transactionManager, contractGasProvider)
        }
    }

