    override fun setApprovalForAll(operator: String, approved: Boolean): RemoteFunctionCall<TransactionReceipt> {
        val function = Function(
            FUNC_SETAPPROVALFORALL,
            listOf(
                Address(operator),
                Bool(approved)
            ),
            Collections.emptyList()
        )
        return executeRemoteCallTransaction(function)
    }

    override fun getApproved(tokenId: Long): RemoteFunctionCall<String> {
        val function = Function(
            FUNC_GETAPPROVED,
            listOf(Uint256(tokenId)),
            listOf(object : TypeReference<Address>() {})
        )
        return executeRemoteCallSingleValueReturn(function, String::class.java)
    }

    override fun isApprovedForAll(owner: String, operator: String): RemoteFunctionCall<Boolean> {
        val function = Function(FUNC_ISAPPROVEDFORALL,
            listOf(
                Address(owner),
                Address(operator)
            ),
            listOf(object: TypeReference<Bool>() {})
        )
        return executeRemoteCallSingleValueReturn(function, Boolean::class.java)
    }

    override fun safeMint(to: String, value: Value, uri: String): RemoteFunctionCall<TransactionReceipt> {
        val function: Function = Function(
            ISwapValue.Functions.FUNC_SAFE_MINT,
            listOf(
                Address(to),
                value,
                Utf8String(uri)),
            Collections.emptyList()
        )
        // omit wei here as it is not a payable function
        return executeRemoteCallTransaction(function)
    }

    override fun safeMint(
        to: String,
        value: Value,
        uri: String,
        wei: BigInteger
    ): RemoteFunctionCall<TransactionReceipt> {
        val function: Function = Function(
            ISwapValue.Functions.FUNC_SAFE_MINT,
            listOf(
                Address(to),
                value,
                Utf8String(uri)),
            Collections.emptyList()
        )
        return executeRemoteCallTransaction(function, wei)
    }

    override fun getOffer(tokenId: String): RemoteFunctionCall<Value> {
        val function: Function = Function(
            ISwapValue.Functions.FUNC_GET_OFFER,
            listOf(
                Uint256(BigInteger(tokenId))
            ),
            listOf(
                object : TypeReference<Value>() {}
            )
        )
        return executeRemoteCallSingleValueReturn(function, Value::class.java)
    }

    override fun getTokensIdsForUser(user: String): RemoteFunctionCall<List<*>> {
        val function =
            Function(
                FUNC_GETTOKENSIDSFORUSER,
                Arrays.asList<Type<*>>(Address(user)),
                Arrays.asList<TypeReference<*>>(object :
                    TypeReference<DynamicArray<Uint256>>() {})
            )
        return RemoteFunctionCall(
            function
        ) {
            val result =
                executeCallSingleValueReturn<Type<Uint256>, List<*>>(
                    function,
                    List::class.java
                ) as List<Type<Uint256>>
            convertToNative<Type<Uint256>, BigInteger>(result)
        }
    }

